        value: 'oracleJdk1.7',
        label: 'Oracle Jdk 1.7'
      }],
      value: '',
      textarea: '',
      form: {
        name: '',
        code: '',
        desc: ''
      },
      rules: {
        name: [
          { required: true, message: '请输入应用名称', trigger: 'blur' }
        ],
        code: [
          { required: true, message: '请输入应用编码', trigger: 'blur' }
        ]
      }

    }
  },
  methods: {
    showEs() {
      this.isEsActive = true
      this.isK8sActive = false
    },
    showK8s() {
      this.isEsActive = false
      this.isK8sActive = true
    },
    showJava() {
      this.isJavaActive = true
      this.isTomcatActive = false
    },
    showTomcat() {
      this.isJavaActive = false
      this.isTomcatActive = true
    },
    nextStep() {
      const tmpStepsActive = this.stepsActive
      if (tmpStepsActive === 0 || tmpStepsActive === 1) {
        this.stepsActive = tmpStepsActive + 1
      }
      this.isShowAppInfo = false
    },
    previousStep() {
      const tmpStepsActive = this.stepsActive
      if (tmpStepsActive !== 0) {
        this.stepsActive = tmpStepsActive - 1
      }
    }
  }
}
</script>

